import re

def pig_latin(text):
    def translate(word):
        vowels = "aeiou"
        if word[0].lower() in vowels:
            return word + "way"
        else:
            consonants = ""
            for letter in word:
                if letter.lower() not in vowels:
                    consonants += letter
                else:
                    break
            return word[len(consonants):] + consonants.lower() + "ay"

    def translate_with_punctuation(word):
        match = re.match(r"([a-zA-Z']+)([^a-zA-Z]*)", word)
        if match:
            translated_word = translate(match.group(1)) + match.group(2)
            return translated_word
        return word

    words = re.findall(r"[\w']+|[.,!?;:]", text)
    translated_words = [translate_with_punctuation(word) for word in words]
    return " ".join(translated_words)